package generator

var headerTmpl = `
package {{.Pkg}}
// Generated by https://github.com/hooklift/gowsdl
// Do not modify
// Copyright (c) 2015, Hooklift. All rights reserved.
import (
    "net/http"
	"encoding/xml"
	"time"

	{{ $pkgBase := .PkgBase }}
	"{{ $pkgBase }}/basetypes"
	{{ range $key, $value := .ResolvedXsdExternals }}
		"{{ $pkgBase }}/{{ getSchemaName $key | replaceReservedWords }}"
	{{end}}

	gowsdl "github.com/hooklift/gowsdl/generator"
)

// against "unused imports"
var _ time.Time
var _ xml.Name
`
